import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthGuard implements CanActivate {

  constructor(private router:Router) {

  }
  canActivate():boolean{
    if(localStorage.getItem('token')!=null){
      return true;
    }
    else{
      // this.router.navigate(['/auth/login']);
