import { Component } from '@angular/core';

@Component({
  selector: 'app-offer-menu2',
  templateUrl: './offer-menu2.component.html',
  styleUrls: ['./offer-menu2.component.scss']
})
export class OfferMenu2Component {

  array=[1,2,3,4];
  addDiv(){
    this.array.push(this.array[this.array.length-1]+1);
  }


}
<p>offer-menu2 works!</p>
<div>
    <button class="btn button bg-primary border border-1 border-secondary" (click)="addDiv()"></button>
</div>
