import { Component } from '@angular/core';
import { DataService } from '../../services/data.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent {

  loginResponseData:any;
  constructor(private dataService: DataService) {
  }

  ngOnInit(){
    console.log(localStorage.getItem(this.dataService.loggedinUser.email));
    this.loginResponseData=this.dataService.loggedinUser;
    // var local=localStorage.getItem(this.dataService.loggedinUser.email);
  }

  dropMenu(){

  }
}
<div class="d-flex flex-row justify-content-between">
    <img src="../../../assets/images/logo.png" class="img-fluid my-4 ms-5" alt="">

    <span class="dropdown">
        <span class="my-4 me-5 fw-bold"> {{this.loginResponseData.firstName}}</span>
        <img src="../../../assets/images/profilepic.png" class="img-fluid my-4 me-5 profile-logo" data-bs-toggle="dropdown" alt="">

        <div class="dropdown-menu ms-3 navbar-menu rounded-0 m-0 p-0 pb-0 text-center">
            <h5 class="dropdown-item navbar-item p-2" routerLink="/acc/profile">Profile</h5>
            <h5 class="dropdown-item navbar-item p-2" routerLink="/acc/myrides">My Rides</h5>
            <h5 class="dropdown-item navbar-item p-2" routerLink="/auth/login" >Logout</h5>    
        </div>
    </span>
</div>
